import { __decorate } from "tslib";
import { Component, Input, Output, EventEmitter } from '@angular/core';
let ButtonComponent = class ButtonComponent {
    constructor() {
        this.label = 'Button';
        this.disabled = true;
        this.color = '#acacac';
        this.clicked = new EventEmitter();
    }
    onClick() {
        if (!this.disabled) {
            this.clicked.emit();
        }
    }
    static { this.propDecorators = {
        label: [{ type: Input }],
        disabled: [{ type: Input }],
        color: [{ type: Input }],
        clicked: [{ type: Output }]
    }; }
};
ButtonComponent = __decorate([
    Component({
        selector: 'app-button',
        template: `
  <button
    type="button"
    [disabled]="disabled"
    (click)="onClick()"
    >
    {{ label }}
  </button>

  <div class="card"></div>

  <div class="alert alert-{{ type }}">
    {{ message }}
  </div>
  `,
        styles: ['']
    })
], ButtonComponent);
export { ButtonComponent };
