import { moduleMetadata } from '@storybook/angular';
import { ButtonComponent } from './button.component';
export default {
    title: 'Button',
    component: ButtonComponent,
    decorators: [
        moduleMetadata({
            declarations: [ButtonComponent],
        }),
    ],
    argTypes: {
        backgroundColor: { control: 'color' },
    },
};
const Template = (args) => ({
    component: ButtonComponent,
    props: args,
});
const Default = {
    template: Template,
    args: {
        label: 'Default',
    },
};
export { Default };
// const Template: StoryObj<ButtonComponentStory> = (args: any) => ({
//   props: args,
// });
// export const Default: StoryObj<ButtonComponentStory> = {
//     template: Template,
//     args: {
//       label: 'Default',
//     },
//   };
export const Hover = {
    template: Template,
    args: {
        label: 'Hover',
        isHover: true,
    },
};
export const Active = {
    template: Template,
    args: {
        label: 'Active',
        isActive: true,
    },
};
