import { ButtonComponent } from './button.component';
export default {
    title: 'Components/Button',
    component: ButtonComponent,
    argTypes: {
        label: { control: { type: 'text' } },
        disabled: { control: { type: 'boolean' } },
        type: { control: { type: 'select', options: ['primary', 'secondary', 'terciary'] } },
    },
};
// export Template
export const TemplateRadioButton = {
    args: {
        label: 'Click Me',
        disabled: false,
    },
};
export const DefaultRadioButton = {
    ...TemplateRadioButton,
    args: {
        disabled: true,
        label: 'Default',
    },
};
export const DisabledRadioButton = {
    ...TemplateRadioButton,
    args: {
        label: 'Disabled',
    }
};
export const SecondaryRadioButton = {
    ...TemplateRadioButton,
    args: {
        label: 'Secondary',
        onClick: () => {
            console.log('secondary button clicked');
        }
    },
};
export const AccentButtonStory = {
    ...TemplateRadioButton,
    args: {
        label: 'terciary',
        onClick: () => {
            console.log('terciary button clicked');
        },
    },
};
