import { ButtonComponent } from './button.component';
import { action } from '@storybook/addon-actions';
export default {
    title: 'Components/Button',
    component: ButtonComponent,
    argTypes: {
        label: { control: { type: 'text' } },
        disabled: { control: { type: 'boolean' } },
        type: { control: { type: 'select', options: ['primary', 'secondary', 'terciary'] } },
    },
};
// export Template
export const TemplateRadioButton = {
    args: {
        label: 'Click Me',
        disabled: false,
    },
};
export const DefaultRadioButton = {
    ...TemplateRadioButton,
    args: {
        disabled: true,
        label: 'Default',
    },
};
export const DisabledRadioButton = {
    ...TemplateRadioButton,
    args: {
        disabled: true,
        label: 'Disabled',
        onClick: action('disable button clicked')
    }
};
export const SecondaryRadioButton = {
    ...TemplateRadioButton,
    args: {
        disabled: false,
        label: 'Secondary',
        onClick: action('secondary button clicked')
    },
};
export const AccentButtonStory = {
    ...TemplateRadioButton,
    args: {
        disabled: false,
        label: 'terciary',
        onClick: action('terciary button clicked'),
    },
};
